<template>  <v-dialog v-model="dialog" scrollable width="60vw">    <PostMailForm></PostMailForm>    <v-dialog width="450" v-model="notify">      <v-card          color="primary"          dark      >        <v-card-text>          Почтовое отправление создано {{ mailOrder }}        </v-card-text>      </v-card>    </v-dialog>  </v-dialog></template><script>import PostMailForm from "@/components/PostMail/PostMailForm";export default {  name: "CreatePostMail",  data: () => ({    dialog: false,    notify: true,    legalData: null,    project: null,    number: null,    doc: null,  }),  methods: {    close() {      Object.assign(this.$data, this.$options.data())      this.dialog = false    },  },  created() {    this.$parent.$on('createPostMail', ({legalData, project, number, doc}) => {      this.legalData = legalData      this.project = project      this.number = number      this.doc = doc      }    )  },  components: {    PostMailForm  }}</script><style scoped>.row {  margin: 0;}</style>